import React,{useState,useEffect} from 'react'
import CommonSection from '../shared/CommonSection'
import '../styles/tour.css';
import TourCard from '../shared/TourCard';
import SearchBar from '../shared/SearchBar';
import tourData from '../assets/data/tours'
import {Container,Row,Col} from 'reactstrap'

const Tours = () => {
  const [pageCount, setPageCount] = useState(0)
  const [page,setPag] = useState(0)

  useEffect(()=>{


    const pages = Math.ceil(5/4)   //this will use in backend
    setPageCount(pages);
  },[page]);


  return (
    <>
    <CommonSection title={"All Tours"}/>
    <section>
      <Container>
        <Row>
          <SearchBar/>
        </Row>
      </Container>
    </section>
   
    <section className='pt-0'> 
      <Container>
      <Row>
        {
          tourData?.map(tour=> (
          <Col lg='3' key={tour.id}>
            <TourCard tour={tour}/>
            </Col>)
       ) }
        <Col lg='12'>
        <div className="pagination d-flex align-item-center justify-content-center mt-4 gap-3">
          {[...Array(pageCount).keys()].map(number=>(
            <span 
            key={number} 
            onClick={()=> setPag(number)}
            className={page===number ? 'active__page' : ''}
            >
              { number + 1 }
            </span>
          ))}

        </div>
        </Col>
      </Row>
      </Container>
    </section>
    </>
  )
  
}

export default Tours
